-- database to list all genres not linked to the show Dexter
SELECT DISTINCT `name` FROM `tv_genres` AS a INNER JOIN `tv_show_genres` AS s ON a.`id` = s.`genre_id` INNER JOIN `tv_shows` AS t ON s.`show_id` = t.`id` WHERE a.`name` NOT IN (SELECT `name` FROM `tv_genres` AS a INNER JOIN `tv_show_genres` AS s ON a.`id` = s.`genre_id`INNER JOIN `tv_shows` AS t ON s.`show_id` = t.`id` WHERE t.`title` = "Dexter") ORDER BY a.`name`;
